package teststudy;

public class VarRange {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Byte Size: "+Byte.SIZE+" bits");
		System.out.println("Byte Type: "+Byte.MIN_VALUE+" ~"+Byte.MAX_VALUE);
		System.out.println("Int Size: "+Integer.SIZE+" bits");
		System.out.println("Int Type: "+Integer.MIN_VALUE+" ~"+Integer.MAX_VALUE);
		
		byte byteNum=127;
		
		System.out.println("byteNum + 1: "+(++byteNum));
		
	}

}
