package java_book;

public class Q6_2_1 {

	public static void main(String[] args) {	
		// TODO Auto-generated method stub
		float calc;
		calc=circle(10);
		
		System.out.println(calc);
		
		
	}
	
	public static float circle(int a)
	{
		float res;
		res=(float) (a*a*3.14);
		
		return res;
		
	}

}
